CREATE DATABASE `bookstore`;

CREATE TABLE bookstore.books(isbn Varchar(255) PRIMARY KEY NOT NULL,category varchar(255) NOT NULL,title Varchar(255) NOT NULL,rating int(1) NOT NULL,author varchar(255) not null,year_published Date NOT NULL,description LONGTEXT NOT NULL,language Varchar(255) NOT NULL,review INT(5) NOT NULL,best_seller boolean NOT NULL,cover_photo varchar(255) NOT NULL);

